<?php$format = get_post_format();if( 'audio' === $format ) {	$this->entry_thumbnail();}elseif( 'video' === $format ) {?><div class="post-video">	<?php $this->entry_thumbnail() ?></div><?php}elseif( 'link' !== $format ) {?><figure class="post-image hmedia">    <?php $this->entry_thumbnail() ?></figure><?php}?><div class="post-contents">    <header>        <div class="post-info">		<?php			$time_string = '<span><time class="published updated" datetime="%1$s">%2$s</time></span>';			printf( $time_string,				esc_attr( get_the_date( 'c' ) ),				get_the_date( 'F d, Y' )			);		?>        </div>		<?php $this->entry_title() ?>    </header>    <footer>        <a href="<?php the_permalink() ?>" class="entry-more btn btn-naked"><span>Continue Reading <i class="fa fa-angle-right"></i></span></a>    </footer></div>